const HandlerCommand = require("./handlercommand");

class SimpleHandlerCommand extends HandlerCommand {
	#args;
	#func;

	constructor(args, func) {
		super(args);

		this.#args = Array.isArray(args.args) ? args.args : [];
		this.#func = func;
	}

	run(interaction, opts, args) {
		this.#func(interaction, opts, args);
	}

	get args() {
		return this.#args;
	}
}

module.exports = SimpleHandlerCommand;
